if [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].key}' | grep type` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].operator}' | grep "In"` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].matchExpressions[].values}' | grep config` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].key}' | grep type` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].operator}' | grep "In"` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAntiAffinity[].matchExpressions[].values}' | grep data` ]] && [[ `kubectl get volumeplacementstrategy statefulset-data -o jsonpath='{.spec.volumeAffinity[].enforcement}' | grep preferred` ]]
